//Converte os dados da imagem de RGB para BGR 9 bits - formato da instrucao wsm
uint16_t converte_em_bgr(uint8_t rgb) {
    uint8_t r, g, b;
    uint16_t bgr;

    //Extrai cada tom (R, G e B) do formato RGB
    r = (rgb >> 5) & 0b111; // 3 bits msb do vermelho
    g = (rgb >> 2) & 0b111; // 3 bits do meio do verde
    b = rgb & 0b11; // 2 bits lsb do azul
    //ajusta o azul de 2 para 3 bits
    b = b << 1;

    //Coloca no formato BBB GGG RRR de 9 bits
    bgr = ( (b << 6) | (g << 3) | r );

    return bgr;
}

//Cria e armazena um sprite na memoria de sprites
void cria_menu(uint16_t dados_do_menu[largura_menu][altura_menu]) {
    uint16_t cor[4800]; //80x60 -> 4800 pixels por sprite
    int y, x;
    int z = 0;
    for ( y = 0; y < altura_menu; y++) {
        for ( x = 0; x < largura_menu; x++) {
            cor[z] = dados_do_menu[y][x]; //Extrai a cor de cada pixel em 9 bits BGR
            z++;
        }
    }
    //Escreve cada pixel da matriz 20x20 na memoria de sprites
    int i, j;
    for ( i = 0; i < largura_menu; i++) {
        for ( j = 0; j < altura_menu; j++) {
        escreve_bloco((i + (j * 80)), cor[i]);
        usleep(1000);
        }
    }
}

int main(){

    inicializa_fpga();

    cria_menu(dados_do_menu);

    fecha_dev_mem();

    return 0;
}